// baleui tag
const buifw_tag = document.getElementsByTagName("buifw");
var buifw_button_tag;
var buifw_input_tag;
var buifw_textarea_tag;
var buifw_card_tag;
var buifw_container_tag;
var buifw_seperator_tag;
var buifw_link_tag;
var buifw_rpanel_tag;
var buifw_gpanel_tag;
var buifw_bpanel_tag;
var buifw_ypanel_tag;
var buifw_widget_title_tag;
var buifw_paragraph_tag;
var buifw_glass_tag;


// rendering widgets
function buifw_render(){
    for (var i = 0;i < buifw_tag.length;i++){
        // buifw_tag[i].classList.add("buifw");
        // button
        buifw_button_tag = buifw_tag[i].getElementsByTagName("button");
        buifw_input_tag = buifw_tag[i].getElementsByTagName("input");
        buifw_textarea_tag = buifw_tag[i].getElementsByTagName("textarea");
        buifw_card_tag = buifw_tag[i].getElementsByTagName("card");
        buifw_container_tag = buifw_tag[i].getElementsByTagName("container");
        buifw_seperator_tag = buifw_tag[i].getElementsByTagName("seperator");
        buifw_link_tag = buifw_tag[i].getElementsByTagName("a");
        buifw_rpanel_tag = buifw_tag[i].getElementsByTagName("rpanel");
        buifw_gpanel_tag = buifw_tag[i].getElementsByTagName("gpanel");
        buifw_bpanel_tag = buifw_tag[i].getElementsByTagName("bpanel");
        buifw_ypanel_tag = buifw_tag[i].getElementsByTagName("ypanel");
        buifw_widget_title_tag = buifw_tag[i].getElementsByTagName("widget_title");
        buifw_paragraph_tag = buifw_tag[i].getElementsByTagName("paragraph");
        buifw_glass_tag = buifw_tag[i].getElementsByTagName("glass");
        // buifw_rpanel_tag = buifw_tag[i].getElementsByTagName("rpanel");

        // proceessing the button tag
        for (var target = 0;target < buifw_button_tag.length;target++){
            buifw_button_tag[target].classList.add("buifw_widget_button");
        }

        // processing the input tag
        for (var target = 0;target < buifw_input_tag.length;target++){
            buifw_input_tag[target].classList.add("buifw_widget_input");
            // buifw_input_tag[target].classList.add("buifw_default_width");
        }

        // processing the textarea tag
        for (var target = 0;target < buifw_textarea_tag.length;target++){
            buifw_textarea_tag[target].classList.add("buifw_widget_input");
        }

        // processing the card tag
        for (var target = 0;target < buifw_card_tag.length;target++){
            // buifw_card_tag[target].classList.add("buifw_widget_card");
            var new_div = document.createElement('div');
            new_div.classList.add('buifw_widget_card');
            new_div.innerHTML = buifw_card_tag[target].innerHTML;
            buifw_card_tag[target].innerHTML = '';
            buifw_card_tag[target].appendChild(new_div);
        }

        // processing the widget_title tag
        for (var target = 0;target < buifw_widget_title_tag.length;target++){
            // buifw_widget_title_tag[target].classList.add("buifw_widget_input");
            buifw_widget_title_tag[target].classList.add("buifw_widget_title_widget");
        }


        // processing the paragraph tag
        for (var target = 0;target < buifw_paragraph_tag.length;target++){
            // buifw_widget_title_tag[target].classList.add("buifw_widget_input");
            buifw_paragraph_tag[target].classList.add("buifw_paragraph_widget");
        }
        

        // processing the seperator tag
        for (var target = 0;target < buifw_seperator_tag.length;target++){
            // buifw_widget_title_tag[target].classList.add("buifw_widget_input");
            buifw_seperator_tag[target].classList.add("buifw_seperator_widget");
            buifw_seperator_tag[target].innerHTML = '<hr>';
        }





        // processing the container tag
        for (var target = 0;target < buifw_container_tag.length;target++){
            // buifw_card_tag[target].classList.add("buifw_widget_card");
            var new_div = document.createElement('div');
            new_div.classList.add('buifw_widget_container');
            new_div.innerHTML = buifw_container_tag[target].innerHTML;
            buifw_container_tag[target].innerHTML = '';
            buifw_container_tag[target].appendChild(new_div);
        }


        // processing the link tag
        for (var target = 0;target < buifw_link_tag.length;target++){
            buifw_link_tag[target].classList.add("buifw_widget_link");
        }



        // processing the rpanel tag
        for (var target = 0;target < buifw_rpanel_tag.length;target++){
            // buifw_card_tag[target].classList.add("buifw_widget_card");
            var new_div = document.createElement('div');
            new_div.classList.add('buifw_widget_rpanel');
            new_div.innerHTML = buifw_rpanel_tag[target].innerHTML;
            buifw_rpanel_tag[target].innerHTML = '';
            buifw_rpanel_tag[target].appendChild(new_div);
        }


        // processing the rpanel tag
        for (var target = 0;target < buifw_gpanel_tag.length;target++){
            // buifw_card_tag[target].classList.add("buifw_widget_card");
            var new_div = document.createElement('div');
            new_div.classList.add('buifw_widget_gpanel');
            new_div.innerHTML = buifw_gpanel_tag[target].innerHTML;
            buifw_gpanel_tag[target].innerHTML = '';
            buifw_gpanel_tag[target].appendChild(new_div);
        }


        // processing the rpanel tag
        for (var target = 0;target < buifw_bpanel_tag.length;target++){
            // buifw_card_tag[target].classList.add("buifw_widget_card");
            var new_div = document.createElement('div');
            new_div.classList.add('buifw_widget_bpanel');
            new_div.innerHTML = buifw_bpanel_tag[target].innerHTML;
            buifw_bpanel_tag[target].innerHTML = '';
            buifw_bpanel_tag[target].appendChild(new_div);
        }

        // processing the rpanel tag
        for (var target = 0;target < buifw_ypanel_tag.length;target++){
            // buifw_card_tag[target].classList.add("buifw_widget_card");
            var new_div = document.createElement('div');
            new_div.classList.add('buifw_widget_ypanel');
            new_div.innerHTML = buifw_ypanel_tag[target].innerHTML;
            buifw_ypanel_tag[target].innerHTML = '';
            buifw_ypanel_tag[target].appendChild(new_div);
        }


        // processing the glass tag
        for (var target = 0;target < buifw_glass_tag.length;target++){
            // buifw_card_tag[target].classList.add("buifw_widget_card");
            var new_div = document.createElement('div');
            new_div.classList.add('buifw_widget_glass');
            new_div.innerHTML = buifw_glass_tag[target].innerHTML;
            buifw_glass_tag[target].innerHTML = '';
            buifw_glass_tag[target].appendChild(new_div);
        }
    }
}


buifw_render();